import "bootstrap/dist/css/bootstrap.min.css";
import '../Styles/ChatComp.css';
import { Avatar } from "@mui/material";
import { ChatBox, ReceiverMessage, SenderMessage } from "mui-chat-box";
import TextField from '@mui/material/TextField';
import { useEffect, useState } from "react";
import Button from "@mui/material/Button";
import SendIcon from '@mui/icons-material/Send';
import APIService from "../Api/APIService";
import { CircularProgress } from "@mui/material";
function ChatComp(){
    const [text,setText] = useState("bla bla")
    const [output, setoutput] = useState("");
    const [waiting, setWaiting] = useState(false);
    const [answer, setAnswer] = useState([]);
    const [question,setquestion] = useState([]);
    const handleSubmit = () => {
        setWaiting(true);
        const tx = question[question.length-1]
        APIService.chat({"question":tx})
          .then((response) => {
            setoutput(response["output"]);
            setWaiting(false);
            setAnswer([...answer,response["output"]])
          })
          .catch((error) => console.log("error", error));
    };
    useEffect(()=>{
        console.log(question)
    },[question])
    return(
        <div >
            <ChatBox>
                {
                    question.forEach((element,i) => {
               
                        return
                        <SenderMessage avatar={<Avatar>NA</Avatar>}>
                            {element}
                        </SenderMessage>
                        
                                })
                            }
            </ChatBox>
            <div>
                <TextField id="standard-basic" label="Standard" variant="standard" value={text} onChange={(target)=>{
                    setText(target.target.value)
                }}/>
                <Button variant="contained" endIcon={<SendIcon />} onClick={() => {
                        setquestion([...question,text])
                        setText("")
                        handleSubmit();
                    }}>
                    Send
                </Button>
            </div>
        </div>
    );
}

export default ChatComp;
