import "bootstrap/dist/css/bootstrap.min.css";
import '../Styles/ChatComp.css';
import { Avatar } from "@mui/material";
import { ChatBox, ReceiverMessage, SenderMessage } from "mui-chat-box";
import TextField from '@mui/material/TextField';
import { useState } from "react";
import Button from "@mui/material/Button";
import SendIcon from '@mui/icons-material/Send';
import APIService from "../Api/APIService";
function ChatComp(){
    const [text,setText] = useState("bla bla")
    const [output, setoutput] = useState("");
    const [waiting, setWaiting] = useState(false);
    const [answer, setAnswer] = useState([]);
    const [question,setquestion] = useState([]);
    const handleSubmit = () => {
        setWaiting(true);
        APIService.chat({text})
          .then((response) => {
            setoutput(response["output"]);
            setWaiting(false);
            setAnswer([...answer,response["output"]])
          })
          .catch((error) => console.log("error", error));
    };
    return(
        <div >
            <ChatBox>
                {
                    question.forEach((element,i) => {
                        return <>
                        <SenderMessage avatar={<Avatar>NA</Avatar>}>
                            {element}
                        </SenderMessage>
                        <ReceiverMessage avatar={<Avatar>KS</Avatar>}>
                            {
                                
                            }
                        </ReceiverMessage>
                        </>
                    })
                }
                <ReceiverMessage avatar={<Avatar>KS</Avatar>}>
                    Hello how are you?
                </ReceiverMessage>
                <SenderMessage avatar={<Avatar>NA</Avatar>}>
                    I'm good thanks you?
                </SenderMessage>
                <ReceiverMessage avatar={<Avatar>KS</Avatar>}>
                    I'm good too!
                </ReceiverMessage>
            </ChatBox>
            <div>
                <TextField id="standard-basic" label="Standard" variant="standard" value={text} onChange={(target)=>{
                    setText(target.target.value)
                }}/>
                <Button variant="contained" endIcon={<SendIcon />} onClick={() => {
                        setquestion([...question,text])
                        setText("")
                    }}>
                    Send
                </Button>
            </div>
        </div>
    );
}

export default ChatComp;
