import "bootstrap/dist/css/bootstrap.min.css";
import '../Styles/ChatComp.css';
import { Avatar } from "@mui/material";
import { ChatBox, ReceiverMessage, SenderMessage } from "mui-chat-box";
import TextField from '@mui/material/TextField';
import { useEffect, useState } from "react";
import Button from "@mui/material/Button";
import SendIcon from '@mui/icons-material/Send';
import APIService from "../Api/APIService";
import { CircularProgress } from "@mui/material";
function ChatComp(){
    const [text,setText] = useState("bla bla")
    const [output, setoutput] = useState("");
    const [waiting, setWaiting] = useState(false);
    const [answer, setAnswer] = useState([]);
    const [question,setquestion] = useState([]);
    const handleSubmit = () => {
        setWaiting(true);
        const tx = question[question.length-1]
        APIService.chat({text:tx})
          .then((response) => {
            setoutput(response["output"]);
            setWaiting(false);
            setAnswer([...answer,response["output"]])
          })
          .catch((error) => console.log("error", error));
    };
    useEffect(()=>{
        console.log(question)
    },[question])
    return(
        <div >
            <ChatBox>
                {
                    question.forEach((element,i) => {
                        return <>
                        <SenderMessage avatar={<Avatar>NA</Avatar>}>
                            {element}
                        </SenderMessage>
                        <ReceiverMessage avatar={<Avatar>KS</Avatar>}>
                        {output === "" && !waiting ? (
                                <></>
                            ) : (
                                <>
                                
                                {waiting ? (
                                    <div
                                    className="d-flex flex-column justify-content-center align-items-center p-4"
                                    style={{ gap: "15px" }}
                                >
                                    <CircularProgress />
                                    {/* <span>This may take some time, please be patient</span> */}
                                </div>
                                ) : (
                                    answer[i]
                                )}
                                </>
                            )}
                                    </ReceiverMessage>
                                    </>
                                })
                            }
                {/* <ReceiverMessage avatar={<Avatar>KS</Avatar>}>
                    Hello how are you?
                </ReceiverMessage>
                <SenderMessage avatar={<Avatar>NA</Avatar>}>
                    I'm good thanks you?
                </SenderMessage>
                <ReceiverMessage avatar={<Avatar>KS</Avatar>}>
                    I'm good too!
                </ReceiverMessage> */}
            </ChatBox>
            <div>
                <TextField id="standard-basic" label="Standard" variant="standard" value={text} onChange={(target)=>{
                    setText(target.target.value)
                }}/>
                <Button variant="contained" endIcon={<SendIcon />} onClick={() => {
                        setquestion([...question,text])
                        setText("")
                        handleSubmit();
                    }}>
                    Send
                </Button>
            </div>
        </div>
    );
}

export default ChatComp;
