import React , {useState,useEffect} from 'react'

import "../Styles/Recycle.css"
import Uploader from '../Components/Uploader'
import APIService from '../Api/APIService'
import { CircularProgress } from "@mui/material";
const Recycle = () => {
    const [img,setImg] = useState([]);
    const [output, setoutput] = useState("");
    const [waiting, setWaiting] = useState(false);
    const [question,setquestion] = useState("what to do with plastic garbage")
    const handleSubmit = () => {
        setWaiting(true);
        APIService.recycle({question})
          .then((response) => {
            setoutput(response["output"]);
            setWaiting(false);
            console.log(response)
          })
          .catch((error) => console.log("error", error));
      };
      const handleReset = () => {
      }
  return (
    <>
    <div className='d-flex justify-content-around errorContainer'>
        <Uploader
        handleChange={(file) => {
        //   var fr = new FileReader();
        //   fr.onload = function () {
        //     TextUpdate(fr.result);
        //   };

        //   fr.readAsText(file);
        handleSubmit()
        
        }}
        handleCancel={(file) => {}}
        name={"file"}
        types={["png","jpg"]}
      />
        
    </div>
    {output === "" && !waiting ? (
        <></>
      ) : (
        <>
          
          {waiting ? (
             <div
             className="d-flex flex-column justify-content-center align-items-center p-4"
             style={{ gap: "15px" }}
           >
             <CircularProgress />
             <span>This may take some time, please be patient</span>
           </div>
          ) : (
            <div>
                <div className='intro-title'>
                    <span>RECYLE YOUR GARBAGE</span>
                </div>
                <div>
                {output}
            </div>
            </div>
           
          )}
        </>
      )}
    </>
    
  )
}

export default Recycle
