                {SubFunctions[FunctionSelected].map((e, i) => {
                  return (
                    <h6 key={"subtitle-"+i}
                      className={
                        SubFunctionSelected == i
                          ? "subfuncActive subfunc "
                          : "subfunc"
                      }
                      onClick={() => {
                        setSubFunctionSelected(i);
                      }}
                    >
                      {" "}
                      {e["title"]}
                    </h6>
                  );
                })}
