                    return (
                      <Link key={"problemtitle-"+i}
                        to={"/Calculator/" + e["title"]}
                        state={{
                          Findex: FunctionSelected,
                          Sindex: SubFunctionSelected,
                          SSindex: i,
                        }}
                      >
                        <h6
                          className={
                            SubSubFunctionSelected == i
                              ? "subsubfuncActive subsubfunc "
                              : "subsubfunc"
                          }
                          onClick={() => {
                            setSubSubFunctionSelected(i);
                          }}
                        >
                          {" "}
                          {TitleList[e["title"]]}
                        </h6>
                      </Link>
                    );
